public class Ajax_Estado{ 

 public void carregaUnicoAjax(HttpServletRequest request, HttpServletResponse response, Connection conn ) throws Exception{  JSONObject retorno = new JSONObject();	PrintWriter out = response.getWriter();  String pk = request.getParameter("id_estado") == null ? "" : request.getParameter("id_estado");   	if (!Utilitario.isNumeric(pk)) { throw new Exception("Parâmetro inválido!"); } 	Estado obj = new Estado(conn);  	obj.setIdEstado( Integer.parseInt(pk) ); 	obj.lista(); 	if(obj.next()){retorno.put("desc_uf", obj.getDescUf() == null ? "" : obj.getDescUf() );retorno.put("desc_estado", obj.getDescEstado() == null ? "" : obj.getDescEstado() ); 	} 	retorno.put("msg", ""); 	retorno.put("msgok", "ok"); 	out.print(retorno.toJSONString()); } public void SaveAjax(HttpServletRequest request, HttpServletResponse response, Connection conn ) throws Exception{  JSONObject retorno = new JSONObject();	PrintWriter out = response.getWriter();  String id_estado = request.getParameter("id_estado") == null ? "" : request.getParameter("id_estado");   String desc_uf = request.getParameter("desc_uf") == null ? "" : request.getParameter("desc_uf");   String desc_estado = request.getParameter("desc_estado") == null ? "" : request.getParameter("desc_estado");   	if (!Utilitario.isNumeric(id_estado)) { throw new Exception("Parâmetro inválido!"); } if(desc_uf.equalsIgnoreCase("")){ throw new Exception("Campo desc_uf esta em branco!"); }  if(desc_estado.equalsIgnoreCase("")){ throw new Exception("Campo desc_estado esta em branco!"); }  	Estado obj = new Estado(conn);  	obj.setIdEstado( Integer.parseInt(id_estado) ); 	obj.setDescUf( desc_uf ); 	obj.setDescEstado( desc_estado ); 	obj.insert(); 	obj.update(); 	retorno.put("msg", ""); 	retorno.put("msgok", "ok"); 	out.print(retorno.toJSONString()); } public void ListaAjax(HttpServletRequest request, HttpServletResponse response, Connection conn ) throws Exception{  JSONObject retorno = new JSONObject();JSONObject objjson = new JSONObject();	PrintWriter out = response.getWriter(); JSONArray array = new JSONArray(); String id_estado = request.getParameter("id_estado") == null ? "" : request.getParameter("id_estado");   String desc_uf = request.getParameter("desc_uf") == null ? "" : request.getParameter("desc_uf");   String desc_estado = request.getParameter("desc_estado") == null ? "" : request.getParameter("desc_estado");   	if (!Utilitario.isNumeric(id_estado)) { throw new Exception("Parâmetro inválido!"); } if(desc_uf.equalsIgnoreCase("")){ throw new Exception("Campo desc_uf esta em branco!"); }  if(desc_estado.equalsIgnoreCase("")){ throw new Exception("Campo desc_estado esta em branco!"); }  	Estado obj = new Estado(conn);  if(!id_estado.equalsIgnoreCase("")){   	obj.setIdEstado( Integer.parseInt(id_estado) ); } if(!desc_uf.equalsIgnoreCase("")){   	obj.setDescUf( desc_uf ); } if(!desc_estado.equalsIgnoreCase("")){   	obj.setDescEstado( desc_estado ); } 	obj.lista(); 	while(obj.next()){objjson = new JSONObject();objjson.put("id_estado", obj.getIdEstado() == null ? "" : obj.getIdEstado() );objjson.put("desc_uf", obj.getDescUf() == null ? "" : obj.getDescUf() );objjson.put("desc_estado", obj.getDescEstado() == null ? "" : obj.getDescEstado() );array.add(objjson); 	} 	retorno.put("lista", array); 	retorno.put("msg", ""); 	retorno.put("msgok", "ok"); 	out.print(retorno.toJSONString()); }
 
 }