public class Ajax_UserImage{ 

 public void carregaUnicoAjax(HttpServletRequest request, HttpServletResponse response, Connection conn ) throws Exception{  JSONObject retorno = new JSONObject();	PrintWriter out = response.getWriter();  String pk = request.getParameter("id_image") == null ? "" : request.getParameter("id_image");   	if (!Utilitario.isNumeric(pk)) { throw new Exception("Parâmetro inválido!"); } 	UserImage obj = new UserImage(conn);  	obj.setIdImage( Long.parseLong(pk) ); 	obj.lista(); 	if(obj.next()){retorno.put("id_usuario", obj.getIdUsuario() == null ? 0 : obj.getIdUsuario() );retorno.put("desc_image", obj.getDescImage() == null ? "" : obj.getDescImage() );retorno.put("desc_path_system", obj.getDescPathSystem() == null ? "" : obj.getDescPathSystem() ); 	} 	retorno.put("msg", ""); 	retorno.put("msgok", "ok"); 	out.print(retorno.toJSONString()); } public void SaveAjax(HttpServletRequest request, HttpServletResponse response, Connection conn ) throws Exception{  JSONObject retorno = new JSONObject();	PrintWriter out = response.getWriter();  String id_image = request.getParameter("id_image") == null ? "" : request.getParameter("id_image");   String id_usuario = request.getParameter("id_usuario") == null ? "" : request.getParameter("id_usuario");   String desc_image = request.getParameter("desc_image") == null ? "" : request.getParameter("desc_image");   String desc_path_system = request.getParameter("desc_path_system") == null ? "" : request.getParameter("desc_path_system");   	if (!Utilitario.isNumeric(id_image)) { throw new Exception("Parâmetro inválido!"); } 	if (!Utilitario.isNumeric(id_usuario)) { throw new Exception("Parâmetro inválido!"); } if(desc_image.equalsIgnoreCase("")){ throw new Exception("Campo desc_image esta em branco!"); }  if(desc_path_system.equalsIgnoreCase("")){ throw new Exception("Campo desc_path_system esta em branco!"); }  	UserImage obj = new UserImage(conn);  	obj.setIdImage( Long.parseLong(id_image) ); 	obj.setIdUsuario( Long.parseLong(id_usuario) ); 	obj.setDescImage( desc_image ); 	obj.setDescPathSystem( desc_path_system ); 	obj.insert(); 	obj.update(); 	retorno.put("msg", ""); 	retorno.put("msgok", "ok"); 	out.print(retorno.toJSONString()); } public void ListaAjax(HttpServletRequest request, HttpServletResponse response, Connection conn ) throws Exception{  JSONObject retorno = new JSONObject();JSONObject objjson = new JSONObject();	PrintWriter out = response.getWriter(); JSONArray array = new JSONArray(); String id_image = request.getParameter("id_image") == null ? "" : request.getParameter("id_image");   String id_usuario = request.getParameter("id_usuario") == null ? "" : request.getParameter("id_usuario");   String desc_image = request.getParameter("desc_image") == null ? "" : request.getParameter("desc_image");   String desc_path_system = request.getParameter("desc_path_system") == null ? "" : request.getParameter("desc_path_system");   	if (!Utilitario.isNumeric(id_image)) { throw new Exception("Parâmetro inválido!"); } 	if (!Utilitario.isNumeric(id_usuario)) { throw new Exception("Parâmetro inválido!"); } if(desc_image.equalsIgnoreCase("")){ throw new Exception("Campo desc_image esta em branco!"); }  if(desc_path_system.equalsIgnoreCase("")){ throw new Exception("Campo desc_path_system esta em branco!"); }  	UserImage obj = new UserImage(conn);  if(!id_image.equalsIgnoreCase("")){   	obj.setIdImage( Long.parseLong(id_image) ); } if(!id_usuario.equalsIgnoreCase("")){   	obj.setIdUsuario( Long.parseLong(id_usuario) ); } if(!desc_image.equalsIgnoreCase("")){   	obj.setDescImage( desc_image ); } if(!desc_path_system.equalsIgnoreCase("")){   	obj.setDescPathSystem( desc_path_system ); } 	obj.lista(); 	while(obj.next()){objjson = new JSONObject();objjson.put("id_image", obj.getIdImage() == null ? 0 : obj.getIdImage() );objjson.put("id_usuario", obj.getIdUsuario() == null ? 0 : obj.getIdUsuario() );objjson.put("desc_image", obj.getDescImage() == null ? "" : obj.getDescImage() );objjson.put("desc_path_system", obj.getDescPathSystem() == null ? "" : obj.getDescPathSystem() );array.add(objjson); 	} 	retorno.put("lista", array); 	retorno.put("msg", ""); 	retorno.put("msgok", "ok"); 	out.print(retorno.toJSONString()); }
 
 }